import React from 'react'
import './UnitSelection.css'
import { useEffect } from 'react'

const UnitSelection = ({selectedUnit, setSelectedUnit, distributedData, filteredData, UnitData, setUnitData, selectedDistribution}) => {
  const calculateByUnit = (data, selectedUnit, property) => {
    const calculated = [];

    for (const yearData of data) {
      const year = yearData.year;
      const yearDataArray = yearData.data;

      if (selectedUnit === "Mean") {
        let total = 0;
        for (const person of yearDataArray) {
          total += person[property];
        }

        const calculate = total / yearDataArray.length;

        const formattedYearData = {
          year: year,
          data: yearDataArray.map(person => ({ ...person, [property]: calculate })),
        };

        calculated.push(formattedYearData);

      } else if (selectedUnit === "Median") {
        const sortedValues = yearDataArray.map(person => person[property]).sort((a, b) => a - b);
        const middleIndex = Math.floor(sortedValues.length / 2);

        let median;
        if (sortedValues.length % 2 === 0) {
          median = (sortedValues[middleIndex - 1] + sortedValues[middleIndex]) / 2;
        } else {
          median = sortedValues[middleIndex];
        }

        const formattedYearData = {
          year: year,
          data: yearDataArray.map(person => ({ ...person, [property]: median })),
        };

        calculated.push(formattedYearData);
      }
    }

    return calculated;
  };



    useEffect(() => {
      const newCalculated = calculateByUnit(filteredData, selectedUnit, selectedDistribution)
      setUnitData(newCalculated)
      console.log("unitData:", UnitData, selectedUnit)
    },[distributedData, filteredData, selectedUnit])



    const handleUnitChange = (event) => {
      setSelectedUnit(event.target.value);
    };

  return (
    <div className='unit'>
    <label htmlFor='units'>Unit</label>
  <div className='units' id='unit'>
  <label>
    <input
    type="radio"
    value="Mean"
    checked={selectedUnit === 'Mean'}
    onChange={handleUnitChange}
    />
    Mean
  </label>

  <label>
    <input
    type="radio"
    value="Median"
    checked={selectedUnit === 'Median'}
    onChange={handleUnitChange}
    />
    Median
 </label>
  </div>
  </div>
  )
}

export default UnitSelection
